import { Mesh, Color, DoubleSide, MeshPhysicalMaterial } from 'three'

export const createChromaticMaterial = () => {
  const chromaticMaterial = new MeshPhysicalMaterial({
    color: new Color(1.0, 0.9, 0.8),
    emissive: new Color(0.1, 0.1, 0.1),
    roughness: 0.45,
    metalness: 0.01,
    clearcoat: 0.01,
    reflectivity: 0.0,
    sheen: 0.5,
    side: DoubleSide,
    specularIntensity: 1,
    name: MaterialName.chromaticMaterial
  })

  return chromaticMaterial
}

const TestPlugin = ({useThreeConetxt}) => {
  const handleOnClick = () => {
    const { camera, scene, gl, invalidate } = useThreeConetxt
    if (scene) {
      scene.traverse((child) => {
        if (child instanceof Mesh && child.isMesh) {
          const object = child
          object.material = getChromaticMaterial
        }
      })
    }
    invalidate()
  }
  return <div onClick={handleOnClick}>Test Plugin</div>
}

export default TestPlugin
